import { useEffect, useState } from "react";
import { BrowserRouter as Router, Switch, Route, NavLink } from "react-router-dom";

function App() {
  const [showLoading, setShowLoading] = useState(true);

  useEffect(() => {
    let delay = setTimeout(() => setShowLoading(false), 1 * 1000);

    return () => {
      clearTimeout(delay);
    };
  }, []);


  return (
    <Router>
      <div className="bg-gray-50 py-10 bg-gradient-to-b from-blue-500 to-blue-800">
        <div className="fixed z-10 w-full top-0 shadow-lg ring-1 bg-white ring-black ring-opacity-5 overflow-hidden">
          <div className="max-w-7xl mx-auto px-8">
            <div className="flex items-center justify-between h-16">
              <div className="flex items-center">
                <NavLink className="flex-shrink-0 text-gray-800 font-medium" to="/">
                  Spending<span className="font-medium text-blue-700">Sight</span>
                </NavLink>
                <div className="hidden md:block">
                  <div className="ml-10 flex items-baseline space-x-4">
                    <NavLink className="text-gray-600 px-3 py-2 rounded-md text-sm font-medium hover:text-blue-800" exact activeClassName="text-blue-700" to="/">
                      HOME
                    </NavLink>
                    <NavLink className="text-gray-600 px-3 py-2 rounded-md text-sm font-medium hover:text-blue-800" activeClassName="text-blue-700" to="/dashboard">
                      DASHBOARD
                    </NavLink>
                  </div>
                </div>
              </div>

            </div>
          </div>

        </div>
        {
          <Switch>
            <Route exact path="/">
              <div className="max-w-7xl mx-auto p-10 mt-16 bg-white rounded-md">
                <Homepage />
              </div>
            </Route>
            <Route path="/signup">
              <SignUp />
            </Route>
            <Route path="/dashboard">
              <div className="max-w-7xl mx-auto px-8 mt-10 pb-10">
                {showLoading ? (
                  <div className="fixed top-0 left-0 w-full h-full bg-gray-50">
                    <span className="absolute top-1/3 left-1/2 animate-spin rounded-full p-2 text-blue-600 text-sm">
                      <svg className="animate-spin h-10 w-10" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                        <circle className="opacity-50" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-100" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                      </svg>
                    </span>
                    <p className="absolute top-1/3 left-1/2 mt-16 text-blue-600 text-sm">Loading...</p>
                  </div>
                ) : (!user ? (
                  <LoginModal />
                ) : (
                  <>
                    <Dashboard />
                  </>
                ))}
              </div>
            </Route>
          </Switch>
        }
      </div>
    </Router>
  );
}

export default App;
